var mongoose = require('mongoose');

var Schema = mongoose.Schema;

var User = require('./user.model');

var QuestionSchema = new Schema({
  question: {
    type: String,
    required: true,
    max: 100
  },
  answer: {
    type: String,
    required: true,
    max: 50
  },
  optionalAnswerOne: {
    type: String,
    max: 50
  },
  optionalAnswerTwo: {
    type: String,
    max: 50
  },
  kind: {
    type: String,
    "enum": ['Perfil', 'Espacial'],
    required: true
  },
  score: {
    type: Number
  },
  user: {
    type: Schema.Types.ObjectId,
    ref: "User"
  }
}); // Export the model

module.exports = mongoose.model('Question', QuestionSchema);
