import React from "react";
// nodejs library to set properties for components
import PropTypes from "prop-types";
// @material-ui/core components
import withStyles from "@material-ui/core/styles/withStyles";

// @material-ui/icons

// core components
import GridContainer from "components/Grid/GridContainer.jsx";
import GridItem from "components/Grid/GridItem.jsx";
import Button from "components/CustomButtons/Button.jsx";
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';

import workStyle from "assets/jss/material-kit-react/views/landingPageSections/workStyle.jsx";

class QuestionsSection extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      profileFile: null,
      familyFile: null,
      height: 100,
      setOpen: React.useState('')
    }
    this.fileSelectedHandler = this.fileSelectedHandler.bind(this)
  }

  handleChange = event =>{
    setAge(event.target.value);
  }

  handleClose = () => {
    setOpen(false);
  }

  handleOpen = () => {
    setOpen(true);
  }

  fileSelectedHandler = event => {
    this.setState({
      profileFile: URL.createObjectURL(event.target.files[0])
    })
    console.log(event.target.files[0]);
  }

  fileSelectedHandlerSecond = event => {
    this.setState({
      familyFile: URL.createObjectURL(event.target.files[0])
    })
    console.log(event.target.files[0]);
  }


  render() {
    const { classes } = this.props;
    var { height } = this.state;
    const [age, setAge] = React.useState('');
    const [open, setOpen] = React.useState(false);
    return (
      <div className={classes.section}>
        <GridContainer justify="center">
          <GridItem cs={12} sm={12} md={8}>

            <h2 className={classes.title}>Compartenos fotos de tu familia</h2>

            <p className={classes.title}>Diseñaremos un album para el paciente, con el fin de que siga conectado 
            con sus familiares, amigos y allegados.</p>
            <form>
              <GridContainer>

                <GridItem xs={12} sm={12} md={12}>
                <div className={classes.typo}>
                    <div className={classes.note}>Selecciona el número de personas que quieres agregar</div>
                  </div>

                  <FormControl className={classes.formControl}>
        <InputLabel htmlFor="demo-controlled-open-select">Age</InputLabel>
        <Select
          open={open}
          onClose={handleClose}
          onOpen={handleOpen}
          value={age}
          onChange={handleChange}
          inputProps={{
            name: 'age',
            id: 'demo-controlled-open-select',
          }}
        >
          <MenuItem value="">
            <em>None</em>
          </MenuItem>
          <MenuItem value={10}>Ten</MenuItem>
          <MenuItem value={20}>Twenty</MenuItem>
          <MenuItem value={30}>Thirty</MenuItem>
        </Select>
      </FormControl>

                  <div className={classes.typo}>
                    <div className={classes.note}>Selecciona el parentesco de la persona con el paciente</div>
                  </div>

                  <input 
                  style={{display:'none'}}
                  type="file" 
                  onChange={this.fileSelectedHandler}
                  ref = {fileInput => this.fileInput = fileInput}
                  />
                  <img src={this.state.profileFile} height={height}/>
                  <Button 
                      onClick={() => this.fileInput.click()}
                      round size="normal"
                      color="success"
                      >Subir foto perfil
                  </Button>

                  <div className={classes.typo}>
                      <div className={classes.note}>Sube una foto del paciente con su familia</div>
                  </div>

                  <input 
                  style={{display:'none'}}
                  type="file" 
                  onChange={this.fileSelectedHandlerSecond}
                  ref = {fileInput2 => this.fileInput2 = fileInput2}
                  />
                  <img src={this.state.familyFile} height={height}/>
                  <Button 
                      onClick={() => this.fileInput2.click()}
                      round size="normal"
                      color="success"
                      >Subir foto familiar
                  </Button>


                <GridContainer justify="center">

              <GridItem xs={12} sm={7} md={3}>
                  <Button 
                  round size="lg"
                  color="success"
                  >Guardar
                  </Button>

                </GridItem>

              </GridContainer>
              </GridItem>

              </GridContainer>


            </form>
          </GridItem>
        </GridContainer>
      </div>
    );
  }
}

QuestionsSection.propTypes = {
  classes: PropTypes.object
};

export default withStyles(workStyle)(QuestionsSection);
