import React from "react";
// nodejs library to set properties for components
import PropTypes from "prop-types";
// @material-ui/core components
import withStyles from "@material-ui/core/styles/withStyles";

// @material-ui/icons

// core components
import GridContainer from "components/Grid/GridContainer.jsx";
import GridItem from "components/Grid/GridItem.jsx";
import CustomInput from "components/CustomInput/CustomInput.jsx";
import Button from "components/CustomButtons/Button.jsx";

import workStyle from "assets/jss/material-kit-react/views/landingPageSections/workStyle.jsx";

class QuestionsSection extends React.Component {
  fileSelectedHandler = event => {
    console.log(event.target.files[0]);
  }

  render() {
    const { classes } = this.props;
    return (
      <div className={classes.section}>
        <GridContainer justify="center">
          <GridItem cs={12} sm={12} md={8}>
            <h2 className={classes.title}>Primero, cuentanos sobre el paciente</h2>

            <p className={classes.title}>Cuentanos información que sea agradable para el paciente.
            Si la respuesta es negativa, desconoces alguna de las respuestas o no estas seguro, deja la respuesta vacía.</p>
            <form>
              <GridContainer>

                <GridItem xs={12} sm={10} md={12}>
                <div className={classes.typo}>
                  <div className={classes.note}>Sube una foto de perfil para el paciente</div>

                </div>

              <input 
              style={{display:'none'}}
              type="file" 
              onChange={this.fileSelectedHandler}
              ref = {fileInput => this.fileInput = fileInput}
              />
              <Button 
                  onClick={() => this.fileInput.click()}
                  round size="normal"
                  color="success"
                  >Subir foto perfil
              </Button>

              <div className={classes.typo}>
                  <div className={classes.note}>Sube una foto del paciente con su familia</div>

                </div>

                <input 
              style={{display:'none'}}
              type="file" 
              onChange={this.fileSelectedHandler}
              ref = {fileInput => this.fileInput = fileInput}
              />
              <Button 
                  onClick={() => this.fileInput.click()}
                  round size="normal"
                  color="success"
                  >Subir foto familiar
              </Button>


                <CustomInput
                  label="Label"
                  labelText="¿Ciudad dónde vive?"
                  id="city"
                  formControlProps={{
                  fullWidth: true
                  }}
                  inputProps={{
                  onChange: this.onChange,
                  type: "text",
                  }}
                />

                <CustomInput
                  labelText="¿Departamento dónde vive?"
                  id="department"
                  formControlProps={{
                  fullWidth: true
                  }}
                  inputProps={{
                  onChange: this.onChange,
                  type: "text",
                  }}
                />

                <CustomInput
                  labelText="¿Barrio dónde vive?"
                  id="neighborhood"
                  formControlProps={{
                  fullWidth: true
                  }}
                  inputProps={{
                  onChange: this.onChange,
                  type: "text",
                  }}
                />

                <CustomInput
                  labelText="¿Dónde nació?"
                  id="birthCity"
                  formControlProps={{
                  fullWidth: true
                  }}
                  inputProps={{
                  onChange: this.onChange,
                  type: "text",
                  }}
                />

                <CustomInput
                  labelText="Profesion que ejerció o ejerce"
                  id="profession"
                  formControlProps={{
                  fullWidth: true
                  }}
                  inputProps={{
                  onChange: this.onChange,
                  type: "text",
                  }}
                />

                <CustomInput
                  labelText="Nombre del último lugar dónde estudió escuela/universidad/instituto"
                  id="school"
                  formControlProps={{
                  fullWidth: true
                  }}
                  inputProps={{
                  onChange: this.onChange,
                  type: "text",
                  }}
                />

                <CustomInput
                  labelText="Sobrenombre"
                  id="nickname"
                  formControlProps={{
                  fullWidth: true
                  }}
                  inputProps={{
                  onChange: this.onChange,
                  type: "text",
                  }}
                />

                <CustomInput
                  labelText="¿Nombre de la pareja?"
                  id="partner"
                  formControlProps={{
                  fullWidth: true
                  }}
                  inputProps={{
                  onChange: this.onChange,
                  type: "text",
                  }}
                />

                <CustomInput
                  labelText="¿Contrajo matrimonio?"
                  id="isMarried"
                  formControlProps={{
                  fullWidth: true
                  }}
                  inputProps={{
                  onChange: this.onChange,
                  type: "text",
                  }}
