var mongoose = require('mongoose');

var Schema = mongoose.Schema;

var Activity = require('./activity.model');

var AlbumSchema = new Schema({
  description: {
    type: String,
    required: true,
    max: 200
  },
  photoSelection: {
    type: String,
    required: true
  },
  score: {
    type: Number
  },
  activity: {
    type: Schema.Types.ObjectId,
    ref: "Activity"
  }
}); // Export the model

module.exports = mongoose.model('Album', AlbumSchema);
