import React from "react";
// nodejs library to set properties for components
import PropTypes from "prop-types";
// @material-ui/core components
import withStyles from "@material-ui/core/styles/withStyles";

// @material-ui/icons
import Check from "@material-ui/icons/Check";

// core components
import GridContainer from "components/Grid/GridContainer.jsx";
import GridItem from "components/Grid/GridItem.jsx";
import CustomInput from "components/CustomInput/CustomInput.jsx";
import Button from "components/CustomButtons/Button.jsx";
import Select from 'react-select';
import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepLabel from '@material-ui/core/StepLabel';
import StepContent from '@material-ui/core/StepContent';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';

import questionStyle from "assets/jss/material-kit-react/views/landingPageSections/questionStyle.jsx";

//Import data
import {towns, defaultChooseTown, optionsDeparment} from "../Data/Deparments";

import { styled } from "@material-ui/styles";

const StyledStepLabel = styled(StepLabel)({

  "& .MuiStepLabel-label": {
    color: "rgba(0,0,0,0.5);",
    fontFamily: "Roboto Slab",
    fontWeight: "600",
    marginBottom: "0px"
  },
  "& .MuiStepLabel-active": {
    color: "#3C4858",
    fontFamily: "Roboto Slab",
    fontWeight: "600"
  },

});

const customStyles = {
  option: (provided, state) => ({
    ...provided,
    fontFamily: "Roboto",
    fontSize: "14px",
    color: state.isSelected ? 'white' : 'black',
    backgroundColor: state.isSelected ? 'indigo' : 'white'
  }),
  control: (provided) => ({
    ...provided,
    marginTop: "5%",
    fontFamily: "Roboto",
    fontSize: "14px",
    color:  "#495057"
  })
}

class QuestionsSection extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      height: 100,
      step: 1, 
      deparment: null,
      number: null,
      town: null,
      step: 0,
    }
  }

  //Procedd next step
nextStep = () => {
  const { step } = this.state;
  this.setState({ step: step + 1 });
  console.log(step);
} 

//Go back previous step
prevStep = () => {
  const { step } = this.state;
  this.setState({ step: step -1 });
} 

getSteps() {
  return ['Información personal', 'Localización actual del paciente', 'Create an ad'];
}

sectionPersonalInf(){
  return <div>
    <p className={this.props.classes.description}>¿La persona tiene algún sobrenombre?</p>


    <CustomInput
      labelText="Sobrenombre"
      id="nickname"
      formControlProps={{
      fullWidth: true
      }}
      inputProps={{
      onChange: this.onChange,
      type: "text",
      }}
    />
  </div>
}

sectionActualLocal(){
  return <div>
    <Select  
        styles = {customStyles} 
        options={optionsDeparment}
        defaultValue={optionsDeparment[0]} 
        onChange={this.handleChange}
      />

    <Select  
      styles = {customStyles} 
      options={towns[this.state.number]}
      defaultValue={defaultChooseTown}
      onChange={this.handleChangeTown}
    />

    <CustomInput
      labelText={"Barrio"}
      id="neighborhood"
      formControlProps={{
      fullWidth: true
      }}
      inputProps={{
      onChange: this.onChange,
      type: "text",
      }}
    />

  </div>
}

getActualSection = (step) => {
  switch (step) {
    case 0:
      return this.sectionPersonalInf()
    case 1:
      return this.sectionActualLocal()
    case 2:
      return `Try out different ad text to see what brings in the most customers,
              and learn how to enhance your ads using features like ad extensions.
              If you run into any problems with your ads, find out how to tell if
              they're running and how to resolve approval issues.`;
    default:
      return 'Unknown step';
  }
}

getButtonsYesOrNot(){}

  handleChange = (deparment) => {
    this.setState({ deparment });
    this.setState({ number: (optionsDeparment.indexOf(deparment)-1)});
  }

  handleChangeTown = (town) => {
    this.setState({ town });
  }


  render() {
    const { classes } = this.props;
    const { step } = this.state; 
    const steps = this.getSteps();
    var { height } = this.state;

    return (
      <div className={classes.section}>
        <GridContainer justify="center">
          <GridItem cs={12} sm={12} md={8}>

            <h2 className={classes.title}>Primero, cuentanos sobre el paciente</h2>

            <p className={classes.title}>Cuentanos información sobre el paciente y algunas otras memorias que le hagan feliz.</p>
            <form>

              <GridContainer>
                <GridItem xs={12} sm={10} md={12}>

                <div >
                <Card class="shadow-lg p-3 mb-5 bg-white rounded" >
                  <CardContent>

                  <p className={classes.simpleText}> {this.getSteps()[this.state.step]}</p>
                  {this.getActualSection(this.state.step)} 

                  <div >
                          <div>
                            <Button
                              disabled={step === 0}
                              onClick={this.prevStep}
                            >
                              Anterior
                            </Button>
                            <Button
                              variant="contained"
                              color="primary"
                              onClick={this.nextStep}
                            >
                              {step === steps.length - 1 ? 'Finalizar' : 'Siguiente'}
                            </Button>
                          </div>
                        </div>

                  </CardContent>
                  <CardActions>
                  </CardActions>
                </Card>
                </div>




              </GridItem>
             </GridContainer>
            </form>
          </GridItem>
        </GridContainer>
      </div>
    );
  }
}

QuestionsSection.propTypes = {
  classes: PropTypes.object
};

export default withStyles(questionStyle)(QuestionsSection);
