var mongoose = require('mongoose');

var Schema = mongoose.Schema;

var User = require('./user.model');

var Activity = require('./activity.model');

var SessionTherapySchema = new Schema({
  sessionDate: {
    type: Date,
    required: true
  },
  minutesDuration: {
    type: Number,
    required: true
  },
  user: {
    type: Schema.Types.ObjectId,
    ref: "User"
  },
  activity: [{
    type: Schema.Types.ObjectId,
    ref: "Activity"
  }]
}); // Export the model

module.exports = mongoose.model('SessionTherapy', SessionTherapySchema);
