import React from "react";
// nodejs library to set properties for components
import PropTypes from "prop-types";
// @material-ui/core components
import withStyles from "@material-ui/core/styles/withStyles";

// @material-ui/icons

// core components
import GridContainer from "components/Grid/GridContainer.jsx";
import GridItem from "components/Grid/GridItem.jsx";
import Button from "components/CustomButtons/Button.jsx";

import Select from 'react-select';
import CustomInput from "components/CustomInput/CustomInput.jsx";


import workStyle from "assets/jss/material-kit-react/views/landingPageSections/workStyle.jsx";
import 'bootstrap/dist/css/bootstrap.css';

const options = [
  { value: 'Madre', label: 'Mamá' },
  { value: 'Padre', label: 'Papá' },
  { value: 'Abuelo', label: 'Abuelo'},
  { value: 'Abuela', label: 'Abuela'},
  { value: 'Tio', label: 'Tio'},
  { value: 'Tia', label: 'Tia'},
  { value: 'Hermano', label: 'Hermano'},
  { value: 'Hermana', label: 'Hermana'},
  { value: 'Sobrino', label: 'Sobrino'},
  { value: 'Sobrina', label: 'Sobrina'},
  { value: 'Esposo', label: 'Esposo'},
  { value: 'Esposa', label: 'Esposa'},
  { value: 'Hijo', label: 'Hijo' },
  { value: 'Hija', label: 'Hija' },
  { value: 'Nieto', label: 'Nieto'},
  { value: 'Nieta', label: 'Nieta'},
  { value: 'Bisnieto', label: 'Bisnieto'},
  { value: 'Bisnieta', label: 'Bisnieta'},
  { value: 'Novio', label: 'Novio'},
  { value: 'Novia', label: 'Novia'},
  { value: 'Yerno', label: 'Yerno'},
  { value: 'Nuera', label: 'Nuera'},
  { value: 'Amigo', label: 'Amigo'},
  { value: 'Amiga', label: 'Amiga'},
  { value: 'Cuidador', label: 'Cuidador'},
  { value: 'Cuidadora', label: 'Cuidadora'},
]

const customStyles = {
  option: (provided, state) => ({
    ...provided,
    color: state.isSelected ? 'white' : 'black',
    backgroundColor: state.isSelected ? 'indigo' : 'white'
  }),
  control: (provided) => ({
    ...provided,
    marginTop: "5%",
  })
}


class QuestionsSection extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      height: 100,
      selectedOption: null,
      name: '',
      last_name: ''
    }
  }


  handleChange = (selectedOption) => {
    this.setState({ selectedOption });
    console.log(`Option selected:`, selectedOption.value);
  }

  onChange = e => {
    this.setState({ [e.target.id]: e.target.value });
  };

  onSubmit = e => {

    const affectiveRelationData = {
      name: this.state.name,
      last_name: this.state.last_name,
    };

    console.log(affectiveRelationData);

    e.preventDefault(); 
  };

  render() {
    const { classes } = this.props;
    var { height } = this.state;
    return (
      <div className={classes.section}>
        <GridContainer justify="center">
          <GridItem cs={12} sm={12} md={8}>

            <h2 className={classes.title}>Compartenos información de la familia</h2>

            <p className={classes.title}>Diseñaremos un album para el paciente, con el fin de que siga conectado 
            con sus familiares, amigos y allegados.</p>
            <form onSubmit={this.onSubmit}>
              <GridContainer>

                <GridItem xs={12} sm={12} md={12}>

                  <div className={classes.typo}>
                    <div className={classes.note}>Selecciona el parentesco de la persona con el paciente</div>
                  </div>

                  <Select  
                  styles = {customStyles} 
                  options={options} 
                  onChange={this.handleChange}
                  />

                  <CustomInput
                    value={this.state.name}
                    labelText="Nombre"
                    id="name"
                    formControlProps={{
                      fullWidth: true
                      }}
                    inputProps={{
                      onChange: this.onChange,
                      type: "text",
                      }}
                  />

                    <CustomInput
                      value={this.state.last_name}
                      labelText="Apellidos"
                      id="last_name"
                      formControlProps={{
                        fullWidth: true
                      }}
                      inputProps={{
                        onChange: this.onChange,
                        type: "text",
                      }}
                    />


                <GridContainer justify="center">

              <GridItem xs={12} sm={7} md={3}>
                  <Button 
                  round size="lg"
                  color="success"
                  type="submit"
                  >Guardar
                  </Button>

                </GridItem>

              </GridContainer>
              </GridItem>

              </GridContainer>


            </form>
          </GridItem>
        </GridContainer>
      </div>
    );
  }
}

QuestionsSection.propTypes = {
  classes: PropTypes.object
};

export default withStyles(workStyle)(QuestionsSection);
